   private void initSynopparameters() 
   {

      // put back earlier inserted values
      //
      
      // wind waves period
      if (wind_waves_period.compareTo("") != 0)
      {   
         jTextField1.setText(wind_waves_period); 
      }   

      // wind waves height
      if (wind_waves_height.compareTo("") != 0)
      {   
         jTextField2.setText(wind_waves_height); 
      }   
      
      // no swell
      if (swell_1_dir.equals("no swell"))
      {
         jRadioButton2.setSelected(true);
      }
      
      // confused swell
      else if (swell_1_dir.equals("confused"))
      {
         jRadioButton3.setSelected(true);
      }

      // one swell disernable
      else if ( (swell_1_dir.compareTo("") != 0) && (swell_2_dir.equals("")) )
      {
         jRadioButton4.setSelected(true);
      }
      
      // two swells disernable
      else if ( (swell_1_dir.compareTo("") != 0) && (swell_2_dir.compareTo("") != 0) )
      {
         jRadioButton5.setSelected(true);
      }
      
      // swell not determined
      else
      {
         jRadioButton1.setSelected(true);
      }
   }


