   private void Reset_All_Waves_Vars()
   {
      // scope this module + myturbowin.java main module (all of type: static)
      wind_waves_period          = "";
      wind_waves_height          = "";
      swell_1_dir                = "";
      swell_1_period             = "";
      swell_1_height             = "";
      swell_2_dir                = "";
      swell_2_period             = "";   
      swell_2_height             = "";
      Hw_code                    = "";
      Pw_code                    = "";
      Dw1_code                   = "";
      Pw1_code                   = "";
      Hw1_code                   = "";
      Dw2_code                   = "";
      Hw2_code                   = "";
 
      
      // local var's with initialisation
      float_wind_waves_height    = main.INVALID;               // could become e.g. 2.5 (-> use float)
      int_wind_waves_period      = main.INVALID;
      checks_ok                  = false;

      /* update wave fields on main (progress)screen */
      main.waves_fields_update();
   }


   
   private void OK_button_actionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OK_button_actionPerformed
// TODO add your handling code here:
       
      // initialisation

      // Only if Swell not determined or Calm sea -> reset all waves vars
      // if onther wave options was chosen -> will be followed by a second appropriate waves input form
      if ((jRadioButton1.isSelected() == true) || (jRadioButton2.isSelected() == true))
      {
         Reset_All_Waves_Vars();
      }
      
      // get text from inserted fields
      wind_waves_period = jTextField1.getText().trim(); 
      wind_waves_height = jTextField2.getText().trim();
        
      
     
      /* 
      //////////////// checks
      */
      
      // convert collected (string) values to ints and floats
      
      // wind waves
      try 
      {
         int_wind_waves_period = Integer.parseInt(wind_waves_period.trim());
      }
      catch (NumberFormatException e){/* ... */}
        
      try 
      {
         float_wind_waves_height = Float.parseFloat(wind_waves_height.trim());
      }
      catch (NumberFormatException e){/* ... */}
      
      
         
      //
      ///////////////// wind waves checks
      //
      if ((wind_waves_period.trim().compareTo("confused") != 0) && (wind_waves_period.trim().length() > 0) && 
          (int_wind_waves_period < 0 || int_wind_waves_period > 50 || int_wind_waves_period == main.INVALID))
      {   
         JOptionPane.showMessageDialog(null, "Wind waves period must be in range 0 - 50 (no decimals), confused or blank", main.APPLICATION_NAME + " error", JOptionPane.WARNING_MESSAGE);
         Reset_All_Waves_Vars();
      }
          
      else if ((wind_waves_height.trim().compareTo("confused") != 0) && (wind_waves_height.trim().length() > 0) && 
               (float_wind_waves_height < 0.0 || float_wind_waves_height > 49.0 || float_wind_waves_height == main.INVALID))
      {
         JOptionPane.showMessageDialog(null, "Wind waves height must be in range 0 - 49.0, confused or blank", main.APPLICATION_NAME + " error", JOptionPane.WARNING_MESSAGE);
         Reset_All_Waves_Vars();
      }
      
      else
      {
          checks_ok = true;
      }
      
      
      
      /*
      ///////////// consistency checks
      */
      if (checks_ok == true)
      {   
         
         ////// wind waves
	      if ((int_wind_waves_period == 0) && (float_wind_waves_height > 2.2) && (float_wind_waves_height != main.INVALID))
	      {
            JOptionPane.showMessageDialog(null, "If 'wind waves period' = 0 sec. then 'wind waves height' can not be > 2.2 metres", main.APPLICATION_NAME + " error", JOptionPane.WARNING_MESSAGE);
            Reset_All_Waves_Vars();
         } // if ((int_wind_waves_period == 0) etc.
         
         else if ((float_wind_waves_height == 0.0) && (wind_waves_period.trim().equals("confused")))
	      {
            JOptionPane.showMessageDialog(null, "If 'wind waves height' = 0 metre then 'wind waves period' can not be > 5 sec. or confused", main.APPLICATION_NAME + " error", JOptionPane.WARNING_MESSAGE);
            Reset_All_Waves_Vars();
         } // if ((int_wind_waves_period == 0) etc.
         
         else if ((float_wind_waves_height == 0.0) && (int_wind_waves_period > 5) && (int_wind_waves_period != main.INVALID) && (wind_waves_height.trim().compareTo("confused") != 0))
	      {
            JOptionPane.showMessageDialog(null, "If 'wind waves height' = 0 metre then 'wind waves period' can not be > 5 sec. or confused", main.APPLICATION_NAME + " error", JOptionPane.WARNING_MESSAGE);
            Reset_All_Waves_Vars();
         } // if ((int_wind_waves_period == 0) etc.
         
      } // if (checks_ok == true)
      
      
      /*
      ///////////// determine meteorological code figures wind waves
      */
      if (checks_ok == true)
      {   
         ///////////// (wind) wave period
         if (wind_waves_period.trim().length() == 0)                             // nothing inserted
         {
            //JOptionPane.showMessageDialog(null, "nothing inserted" , "wind waves period", JOptionPane.INFORMATION_MESSAGE);
            Pw_code = "//";
         }
         else if (wind_waves_period.trim().equals("confused"))
         {
            Pw_code = "99";
         }   
         else
         {   
            // nb in this stage always in whole seconds (otherwise earlier error message) 
            Pw_code = Integer.toString(int_wind_waves_period);     // convert to string      // or (alternative):Pw_code = String.valueOf(num_Pw_code);
            
            // Pw code always 2 characters width e.g. 03
            int len = 2;
            if (Pw_code.length() < len)                                            // pad on left with zeros
               Pw_code = "0000000000".substring(0, len - Pw_code.length()) + Pw_code;

         } // else
         

         //////////// (wind) wave height
         if (wind_waves_height.trim().length() == 0)                             // nothing inserted
         {
            Hw_code = "//";
         }
         else if (wind_waves_height.trim().equals("confused"))
         {
            Hw_code = "//";
         }   
         else
         {   
            // round to units of 0.5 metres
            
            // e.g. 2.2 metres -> Hw = 4 units of 0.5 metres
            // e.g. 2.3 metres -> Hw = 5 units of 0.5 metres
            
            int num_Hw_code = (int)Math.floor(float_wind_waves_height * 2 + 0.5);
            Hw_code = Integer.toString(num_Hw_code);     // convert to string      // or (alternative): Hw_code = String.valueOf(num_Hw_code);
            
            // Hw code always 2 characters width e.g. 03
            int len = 2;
            if (Hw_code.length() < len)                                            // pad on left with zeros
               Hw_code = "0000000000".substring(0, len - Hw_code.length()) + Hw_code;
         } // else
        
          
      } // if (checks_ok == true)  



      
      //
      ////////// swell system(s)
      //
      if (checks_ok == true)
      {
         /* update wave fields on main (progress)screen */
         main.waves_fields_update();

         /* close this input page */
         setVisible(false);
         dispose();

         if (jRadioButton1.isSelected() == true)              // Swell not determined
         {
            // Swell not determined
            //
            // nothing to be done...

            /* next screen if in_next_screen mode */
            if (main.in_next_sequence == true)
            {
               next_screen();
            }
         }
         else if (jRadioButton2.isSelected() == true)         // Calm sea, no swell
         {
            // Calm sea, no swell
            Dw1_code          = "00";
            Dw2_code          = "00";
            //Pw1_code          = "00";
            //Hw1_code          = "00";
         
            swell_1_dir       = "no swell";
            swell_1_period    = "no swell";
            swell_1_height    = "no swell";

            /* next screen if in_next_screen mode */
            if (main.in_next_sequence == true)
            {
               next_screen();
            }
         }
         else if (jRadioButton3.isSelected() == true)           // Confused swell or indeterminable direction
         {
            // Confused swell or indeterminable direction
            myconfusedswell form = new myconfusedswell();    
            form.setSize(800, 600);
            form.setVisible(true);
         }
         else if (jRadioButton4.isSelected() == true)           // One swell discernable
         {
            // One swell discernable
            myoneswellsystem form = new myoneswellsystem();
            form.setSize(800, 600);
            form.setVisible(true);
         }
         else if (jRadioButton5.isSelected() == true)           // Two swells discernable
         {
            // Two swells discernable
            mytwoswellsystems form = new mytwoswellsystems();
            form.setSize(800, 600);
            form.setVisible(true);
         }
      } // if (checks_ok == true)
      
      
      /*
      ////////// Update and close
      */
/*
      if (checks_ok == true)
      {
         // update wave fields on main (progress)screen 
         main.waves_fields_update();

         // close this input page 
         setVisible(false);
         dispose();

      } // if (checks_ok == true)
*/
   }//GEN-LAST:event_OK_button_actionPerformed




